import React from 'react';

import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';

import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => {
  return ({
    icon: {
      marginRight: theme.spacing(2),
    },
    cardGrid: {
      paddingTop: theme.spacing(8),
      paddingBottom: theme.spacing(8),
    },
    card: {
      height: '100%',
      display: 'flex',
      flexDirection: 'column',
    },
    cardMedia: {
      paddingTop: '56.25%',
    },
    cardContent: {
      flexGrow: 1,
    },
  });
});

const cards = [1, 2, 3, 4, 5, 6];

const imgs = ['./imgs/ru_white_1.jpg', './imgs/ru_white_1.jpg', './imgs/ru_white_1.jpg', './imgs/ru_white_1.jpg', './imgs/ru_white_1.jpg', './imgs/ru_white_1.jpg'];
const programs = [
    [1, './imgs/ru_white_1.jpg', 'Tasks for kids in age 3 - 6 years for comparision of one-digit numbers (from 0 to 9), where you can to use only more (>), less (<) and equal (=) labels and have 10 seconds timeout to solve each task'],
    [2, './imgs/ru_white_1.jpg', 'Tasks for kids in age 3 - 6 years for addition and subtraction of one-digit numbers. The result of addition can be an one or two digit number, the result of subtraction is zero or a one-digit number'],
    [3, './imgs/ru_white_1.jpg', 'Tasks for kids in age 3 - 6 years for addition and subtraction of one-digit numbers. The result of addition can be an one or two digit number, the result of subtraction is zero or a one-digit number'],
    [4, './imgs/ru_white_1.jpg', 'Tasks for kids in age 3 - 6 years for addition and subtraction of one-digit numbers. The result of addition can be an one or two digit number, the result of subtraction is zero or a one-digit number'],
    [5, './imgs/ru_white_1.jpg', 'Tasks for kids in age 3 - 6 years for addition and subtraction of one-digit numbers. The result of addition can be an one or two digit number, the result of subtraction is zero or a one-digit number'],
    [6, './imgs/ru_white_1.jpg', 'Tasks for kids in age 3 - 6 years for addition and subtraction of one-digit numbers. The result of addition can be an one or two digit number, the result of subtraction is zero or a one-digit number'],
];

//             {cards.map(card => (

export default function SMAlbum() {
  const classes = useStyles();

  return (
      <main>
        <Container className={classes.cardGrid} maxWidth="md">
          <Grid container spacing={4}>
            {cards.map(card => (
              <Grid item key={card} xs={12} sm={6} md={4}>
                <Card className={classes.card}>
                  <CardMedia
                    className={classes.cardMedia}
                    image={require('./imgs/ru_white_1.jpg')}
                    title="Image title"/>
                  <CardContent className={classes.cardContent}>
                    <Typography gutterBottom variant="h5" component="h2">
                      imgs[card]
                    </Typography>
                    <Typography>
                      Test TEST
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button size="small" color="primary">
                      View
                    </Button>
                    <Button size="small" color="primary">
                      Play
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </main>
  );
}
