import React from 'react';
import {Typography, AppBar, Button, Toolbar, Dialog} from '@material-ui/core';
import {Card, CardMedia, Grid, Link} from '@material-ui/core';

import HomeIcon from '@material-ui/icons/Home';
import InfoOutlinedIcon from '@material-ui/icons/InfoOutlined';
import HelpOutlineIcon from '@material-ui/icons/HelpOutline';
import PersonOutlineIcon from '@material-ui/icons/PersonOutline';

import PopupState, { bindTrigger, bindMenu } from 'material-ui-popup-state';
import {Menu, MenuItem} from '@material-ui/core';

import SMHelp from "./help";
import SMAbout from "./about";
import SMLogin from "./login";

export default class SMHeader extends React.Component {
    constructor(props) {
        super(props);

        this.state = {aboutOpen: false, helpOpen: false, loginOpen: false};

        // this.handleInfoOpen = this.handleInfoOpen.bind(this);
        // this.handleInfoClose = this.handleInfoClose.bind(this);

        this.handleHelpOpen = this.handleHelpOpen.bind(this);
        // this.handleHelpClose = this.handleHelpClose.bind(this);

        this.handleLoginOpen = this.handleLoginOpen.bind(this);
        this.handleLoginClose = this.handleLoginClose.bind(this);
    }

    handleAboutClose() {this.setState({aboutOpen: false});}

    // handleHelpOpen(e) {this.setState({helpOpen: true});}
    // handleHelpClose() {this.setState({helpOpen: false});}

    handleLoginOpen(e) {this.setState({loginOpen: true});}
    handleLoginClose() {this.setState({loginOpen: false});}

    render() {
        return (
          <AppBar position="static">
            <Toolbar>
                <Button variant="contained" color="primary" startIcon={<HomeIcon />}>Home</Button>
                <Button variant="contained" color="primary" startIcon={<InfoOutlinedIcon />} onClick={() => this.setState({aboutOpen: true})}>Info</Button>
                <Button variant="contained" color="primary" startIcon={<HelpOutlineIcon />} onClick={() => this.setState({helpOpen: true})}>Help</Button>

                <Button><img src={require('./../../images/supermath.png')} alt='supermath'/></Button>

                <Button variant="contained" color="primary" startIcon={<PersonOutlineIcon />} onClick={(e) => this.handleLoginOpen(e)}>Login</Button>

                <PopupState variant="popover" popupId="demo-popup-menu">
                  {popupState => (
                    <React.Fragment>
                      <Button variant="contained" color="primary" {...bindTrigger(popupState)}>Language</Button>
                          <Menu {...bindMenu(popupState)}>
                            <MenuItem onClick={popupState.close}>English</MenuItem>
                            <MenuItem onClick={popupState.close}>Russian</MenuItem>
                            <MenuItem onClick={popupState.close}>Dutch</MenuItem>
                          </Menu>
                    </React.Fragment>
                  )}
                </PopupState>

            </Toolbar>

            <SMHelp open={this.state.helpOpen} onClick={() => this.setState({helpOpen: false})}/>
            <SMAbout open={this.state.aboutOpen} />
            <SMLogin open={this.state.loginOpen} />
          </AppBar>
        )
    };
}
