import React from 'react';
import {Dialog, DialogTitle, DialogContent, Paper, Typography} from '@material-ui/core';

import SMKeyBoard from "./../keyboard/keyboard";

import './simplegame.css';

export default class SMSimpleGame extends React.Component {
    constructor(props) {
        super(props);
        this.onDigit = this.onDigit.bind(this);
        this.onOperator = this.onOperator.bind(this);
    }

    onDigit({ target }) {
        const digit = target.innerText;
    }

    onOperator({ target }) {
        const operator = target.innerText;
    }

    render() {
        const topDivLeft = {
            display:'flex',
            alignItems: 'center',
            height: '100%',
            width: '49%',
            paddingLeft: '1%',
            fontFamily: "Grinched",
            fontSize: "1.75rem",
            color: "green",
            float: 'left',

            border: '1px solid red',
        };
        const topDivRight = {
            display:'flex',
            justifyContent:'center',
            alignItems: 'center',
            height: '100%',
            width: '49%',
            paddingLeft: '1%',
            fontFamily: "Grinched",
            fontSize: "1.75rem",
            color: "orange",
            float: 'right',

            border: '1px solid red',
        };
        const leftHalfDiv = {
            height: '90%',
            width: '49%',
            float: 'left',
            border: '3px solid green',
        }
        const rightHalfDiv = {
            display:'flex',
            justifyContent:'center',
            alignItems:'center',

            height: '90%',
            width: '49%',
            float: 'right',
            border: '3px solid red',
        }

        /*
            this.props.open
            https://about.phamvanlam.com/calculator/
        */
        return (
            <Dialog onClose={() => this.props.onClick()} fullScreen={true} open={'true'}>
                <div className="wrapper">
                    <div style={topDiv}>
                        <div style={topDivLeft}>SUPERMATH</div>
                        <div style={topDivRight}>smile</div>
                    </div>

                    <div style={leftHalfDiv}>
                        TBD...2+2=4
                    </div>

                    <div style={rightHalfDiv}>
                        <SMKeyBoard onDigit={this.onDigit} onOperator={this.onOperator}/>
                    </div>
                </div>
            </Dialog>
        );
    }
}
